from __future__ import annotations

# BOF imports
from core.gunnershell.bofs.base import Bof, register

from colorama import Style, Fore
brightgreen = "\001" + Style.BRIGHT + Fore.GREEN + "\002"
brightyellow = "\001" + Style.BRIGHT + Fore.YELLOW + "\002"
reset = Style.RESET_ALL

@register("netuserenum")
class NetUserEnumBof(Bof):
	base64bof: str = ""
	x86base64bof: str = ""

	def help_menu():
		menu = """
This command lists user accounts on the current computer
You may specify one of (all, active, locked, disabled) to filter accounts returned defaults to all if not specified

Usage:
  bofexec netuserenum -z all
  bofexec netuserenum -z active
  bofexec netuserenum -z locked
  bofexec netuserenum -z disabled
"""
		
		print(brightgreen + menu + reset)
		return
